import React, { useEffect } from 'react'

const LoginSignUp = () => {

  const [email, setEmail] = useState("");
    const [password, setPassword] = use("");
    const [auth, setAuth] = useAuth();
    const navigate = useNavigate();
    const location = useLocation();
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                `${process.env.REACT_APP_API}/admin/signin`, { email, password }
            );
            if (response.data.success) {
                setAuth({
                    ...auth,
                    user: response.data.user,
                    token: response.data.token
                })
                localStorage.setItem("auth", JSON.stringify(response.data))
                navigate(location.state || "/dashboard");
            } else {
                alert("Login Failed");
            }
        } catch (err) {
            console.log(err);
        }
    }

    useEffect(()=>{
        const sign_in_btn = document.querySelector("#sign-in-btn");
const sign_up_btn = document.querySelector("#sign-up-btn");
const container = document.querySelector(".container");
console.log("hello");

console.log(sign_up_btn);
sign_up_btn.addEventListener('click', () =>{
    container.classList.add("sign-up-mode");
    console.log("hello");
});

sign_in_btn.addEventListener('click', () =>{
    container.classList.remove("sign-up-mode");
});
    }, [])
  return (
    <div className="container" style={
        {
            margin: 0,
    minWidth: "-webkit-fill-available"
        }
    }>
  <div className="forms-container">
    <div className="signin-signup">
      <form className="sign-in-form">
        <h2 className="title">Sign In</h2>
        <div className="input-field">
          <i className="fas fa-user" />
           <input type="name" value={name} onChange={(e) => setEmail(e.target.value)} className="form-control" id="exampleInputEmail1" placeholder="Enter your email" required />
        </div>

        <div className="input-field">
          <i className="fas fa-lock" />
           <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="exampleInputPassword1" placeholder="Password" />
        </div>
        <input type="submit" defaultValue="Login" className="btn solid" />
        <p className="social-text">Or Sign in with social platforms</p>
        <div className="social-media">
          <a href="#" className="social-icon">
            <i className="fab fa-facebook-f" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-twitter" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-google" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-linkedin-in" />
          </a>
        </div>
      </form>
      <form  className="sign-up-form">
        <h2 className="title">Sign Up</h2>
        <div className="input-field">
          <i className="fas fa-user" />
          <input type="text" placeholder="Username" />
        </div>
        <div className="input-field">
          <i className="fas fa-user" />
          <input type="text" placeholder="Email ID" />
        </div>
        <div className="input-field">
          <i className="fas fa-user" />
          <input type="text" placeholder="Codeforces ID" />
        </div>
        <div className="input-field">
          <i className="fas fa-lock" />
          <input type="password" placeholder="Password" />
        </div>
        <input type="submit" defaultValue="Sign Up" className="btn solid" />
        <p className="social-text">Or Sign up with social platforms</p>
        <div className="social-media">
          <a href="#" className="social-icon">
            <i className="fab fa-facebook-f" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-twitter" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-google" />
          </a>
          <a href="#" className="social-icon">
            <i className="fab fa-linkedin-in" />
          </a>
        </div>
      </form>
    </div>
  </div>
  <div className="panels-container">
    <div className="panel left-panel">
      <div className="content">
        <h3>New here?</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Optio minus natus est.</p>
        <button className="btn transparent" id="sign-up-btn">Sign Up</button>
      </div>
      <img src="./img/log.svg" className="image" />
    </div>
    <div className="panel right-panel">
      <div className="content">
        <h3>One of us?</h3>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Optio minus natus est.</p>
        <button className="btn transparent" id="sign-in-btn">Sign In</button>
      </div>
      <img src="./img/register.svg" className="image" />
    </div>
  </div>
</div>

  )
}

export default LoginSignUp
